    :color :green}
   {:grapes [{:rotten? true :clean? false}
             {:rotten? false :clean? false}]
    :color :black}])

(require '[cljs.test :as t])
(t/deftest i-pass (t/is true))
(meta #'i-pass) ;; => {:ns cljs.user, ;; :name i-pass, ;; :file "NO_SOURCE_FILE", ;; :source "i-pass", ;; :column 12, ;; :end-column 18, ;; :line 1, ;; :end-line 1, ;; :arglists (), ;; :doc "A function that adds two numbers.", ;; :test #<function (){ ... }>}

(def map-without-metadata {:language "ClojureScript"}) ;; => {:language "ClojureScript"}
(meta map-without-metadata) ;; => nil
(def map-with-metadata (with-meta map-without-metadata {:answer-to-everything 42})) ;; => {:language "ClojureScript"}
(meta map-with-metadata) ;; => {:answer-to-everything 42}
(= map-with-metadata map-without-metadata) ;; => true
(identical? map-with-metadata map-without-metadata) ;; => false

(def ^{:doc "The answer to Life, Universe and Everything."} answer-to-everything 42) ;; => 42
(meta #'answer-to-everything) ;; => {:ns cljs.user, ;; :name answer-to-everything, ;; :file "NO_SOURCE_FILE", ;; :source "answer-to-everything", ;; :column 6, ;; :end-column 26, ;; :line 1, ;; :end-line 1, ;; :arglists (), ;; :doc "The answer to Life, Universe and Everything.", ;; :test nil}
(def map-with-metadata ^{:answer-to-everything 42} {:language "ClojureScript"}) ;; => {:language "ClojureScript"}
(meta map-with-metadata) ;; => {:answer-to-everything 42}

(def map-with-metadata ^{:foo 40} {:language "ClojureScript"})
;; => {:language "ClojureScript"}

(meta map-with-metadata)
;; => {:foo 40}

(def derived-map (vary-meta map-with-metadata update :foo + 2))
;; => {:language "ClojureScript"}

(meta derived-map)
;; => {:foo 42}

(def map-with-metadata ^{:foo 40} {:language "ClojureScript"})
;; => {:language "ClojureScript"}

(meta map-with-metadata)
;; => {:foo 40}

(alter-meta! map-with-metadata update :foo + 2)
;; => {:foo 42}

(meta map-with-metadata)
;; => {:foo 42}

(reset-meta! map-with-metadata {:foo 40})
;; => {:foo 40}

(meta map-with-metadata)
;; => {:foo 40}

(deftype Pair [fst snd meta] IMeta (-meta [p] meta)
IWithMeta (-with-meta [p new-meta] (Pair. fst snd new-meta)))
(def p (Pair. 1 2 {:foo :bar})) ;; => #<Pair 1,2>
(meta p) ;; => {:foo :bar}
(def p' (with-meta p {:bar :baz})) ;; => #<Pair 1,2>
(meta p') ;; => {:bar :baz}
